from rest_framework import serializersfrom posts.models import Post, User, Commentclass UserSerializers(serializers.ModelSerializer):    class Meta:        model = User        fields = ('username', )class PostSerializers(serializers.ModelSerializer):    author = serializers.ReadOnlyField(source='author.username')    class Meta:        model = Post        fields = ('id', 'text', 'author', 'pub_date')class CommentSerializers(serializers.ModelSerializer):    author = serializers.ReadOnlyField(source='author.username')    post = serializers.ReadOnlyField(source='post.id')    class Meta:        model = Comment        fields = ('id', 'post', 'text', 'author', 'created')