from django.urls import path, includefrom rest_framework.authtoken import viewsfrom api.views import CommentViewSetfrom .router import routerurlpatterns = [    path('api/v1/api-token-auth/', views.obtain_auth_token),    path('', include(router.urls)),    path('api/v1/posts/<int:pk>/comments/<int:comment_pk>/', CommentViewSet.as_view({        'get': 'list',        'post': 'create',        'put': 'update',        'patch': 'update',        'delete': 'destroy',    })),    path('api/v1/posts/<int:pk>/comments/', CommentViewSet.as_view({        'get': 'list',        'post': 'create',    })),]